export function searchAPI(transientName, physicalPhenomena, messengerType) {
    // Define the base URL and headers
    const baseURL = 'https://lm-astronomy.labs.jb.gg/api/search/'; // TODO: move to .env
    const headers = {
        'accept': 'application/json'
    };
    // if inputs are empty string, replace it with null
    function constructURL(base, params) {
        const filteredParams = Object.entries(params).filter(([key, value]) => value !== null);
        if (!filteredParams.length) {
            return base;
        }
        const queryString = new URLSearchParams(filteredParams).toString().replace(/\+/g, '%20');
        return `${base}?${queryString}`;
    }

// Присваиваем значения параметрам
    transientName = transientName || null;
    physicalPhenomena = physicalPhenomena || null;
    messengerType = messengerType || null;

// Создаем URL
    let url = constructURL(baseURL, {
        transient_name: transientName,
        object_type: physicalPhenomena,
        messenger_type: messengerType
    });

    console.log(url);


    // Make the request
    return fetch(url, { headers })
        .then(response => response.json())
        .then(data => {
            console.log(data);
            return data;
        })
        .catch(error => {
            console.error("Error making request:", error);
        });
}
